mapper.select:
SELECT s.storehouse_id AS id, p.p_id AS goodsId, p.name AS goodsName, s.batchno AS lotNo, p.standard AS specification, CONVERT(VARCHAR(100), p.validday, 23) AS validityDate, CONVERT(VARCHAR(100), s.makedate, 23) AS productionDate, p.unit1Name AS unit, s.quantity AS amount, p.Factory AS manufacturer, l.loc_code AS locationCode, p.barcode AS goodsCode FROM ${account}.dbo.VW_PDA_Storehouse s LEFT JOIN ${account}.dbo.pda_location l ON l.l_id = s.location_id LEFT JOIN ${account}.dbo.pda_Products p ON p.p_id = s.p_id
l.loc_code = #{location}
AND (p.barcode = #{goods} OR p.pinyin LIKE '%' + #{goods} + '%' OR p.name LIKE '%' + #{goods} + '%')
