mapper.select:
SELECT d.storehouse_id AS id, p.name AS goodsName, l.loc_name AS location, d.Batchno AS lotNo, d.quantity AS amount, d.Validdate AS validateDate, p.specification AS specification, p.Factory AS manufacturer, d.pdastates AS status, d.Batchno AS lotNo, p.unit1Name AS unit, CONVERT(varchar(100), d.MakeDate, 23) AS productDate FROM ${account}.dbo.pda_kcpdBill_D d LEFT JOIN ${account}.dbo.pda_Products p ON p.p_id=d.p_id LEFT JOIN ${account}.dbo.pda_location l ON l.l_id=d.Location_id WHERE d.ss_id = #{sId} AND d.DealStates=0
p.barcode = #{product}
AND l.loc_code = #{location}
