mapper.select:
SELECT d.smb_id AS id, d.original_id AS originalId, d.DealStates AS status, p.p_id AS goodsId, p.name AS goodsName, d.Batchno AS lotNo, p.standard AS specification, d.MakeDate AS productionDate, p.unit1Name AS unit, d.EligibleQty AS amount, p.Factory AS manufacturer, l.loc_code AS locationCode, p.barcode AS goodsCode, p.pinyin AS pinyin FROM ${account}.dbo.pda_PutOnBill_D d INNER JOIN ${account}.dbo.pda_PutOnBill b ON d.bill_id = b.billid AND b.pdastates != 2 LEFT JOIN ${account}.dbo.pda_location l ON l.l_id = d.Location_id LEFT JOIN ${account}.dbo.pda_Products p ON p.p_id = d.p_id WHERE b.billnumber = #{orderNoId}
AND d.S_id=#{sId}
ORDER BY l.loc_name ASC

mapper.update:
UPDATE pda_PutOnBill_D SET DealStates=1
WHERE bill_id = (SELECT b.billid FROM pda_pdBill b WHERE b.billid=#{orderNoId})

    AND Location_id = #{allocationId}


    AND p_id = #{goodsId}

mapper.select:
SELECT b.billid AS id, b.billnumber AS name FROM ${account}.dbo.pda_PutOnBill b
WHERE b.pdastates !=2

    AND b.sa_id=#{saId}


    AND b.s_id=#{sId}

SELECT DISTINCT l.l_id AS id, l.loc_name AS name
FROM ${account}.dbo.pda_PutOnBill_D d LEFT JOIN ${account}.dbo.pda_location l ON d.Location_id=l.l_id


        d.S_id=#{sId}

SELECT b.billid AS id, b.billnumber AS documentNo, CONVERT(VARCHAR (100), b.pdaInTime, 23) AS documentDate, 2 AS type, '上架单' AS typeDescription, CASE b.billstates WHEN 10 THEN '待上架' WHEN 13 THEN '已上架' ELSE '' END AS status FROM ${account}.dbo.pda_PutOnBill b WHERE b.pdastates = 1
AND b.s_id=#{sId}
ORDER BY documentDate DESC
